// GENERAL
body{
    transition: padding-top .3s linear;
    color:#383944;
}
.body {
    margin-top: 0 !important;
}
h1, h2{
    text-transform: capitalize;
}
h3{
    font-family:Avenir,Arial,Helvetica,sans-serif;
}
p{
    font-size:16px;
    line-height: 24px;
    font-family: "Avenir", Arial, Helvetica, sans-serif;
    color: #505050;
}
.bavi-bg{
    background:#fff;
    -moz-box-shadow: 0px 4px 4px rgba(0, 0, 0, 0.08);
  -webkit-box-shadow: 0px 4px 4px rgba(0, 0, 0, 0.08);
    box-shadow: 0px 4px 4px rgba(0, 0, 0, 0.08);
}
.logo-left{
    float:left;
    padding: 17px 0;
}
.navi-right{
    float: right;
}
.main-heading{
    font-family: $headings-font;
    font-size: 26px;
    text-transform: none;
    line-height: normal;
}
.page-heading{
    color: stencilColor("color-textHeading");
    font-family: $headings-font;
    font-weight: 500;
    font-size: 38px;
    color:#1B1D1F;
}
.sub-title{
    font-size: 16px;
    color: #373737;
    letter-spacing: 2px;
    text-transform: uppercase;
}
.instagram-section .eapps-instagram-feed-title {
    font-size: 32px !important;
    text-align: center;
    line-height: normal;
    padding: 2px 10px 40px;
    color: #4e4e4e !important;
    font-family: "Didot" !important;
    font-weight: 700 !important;
}
.instagram-section div.eapps-instagram-feed> a{
    pointer-events: none;
}
.instagram-section div.eapps-instagram-feed> a::after{
    content: '';
    background: #fff;
    width: 100%;
    height: 30px;
    position: absolute;
    left: 0;
    top: 0;
}
.uppercase{text-transform: uppercase;}
.center{text-align: center;}
a{text-decoration: none;}

.breadcrumbs{display: block;padding-top: 1.5rem;}

.breadcrumbs,
.body:not(.home) .page-heading{text-align: left;}

.button{
    padding: 1rem 2rem;
    text-transform: uppercase;
    font-weight: 400;
    font-size: 16px;
    border-radius: 0;
    letter-spacing: 2px;
    background: #222125;
}
.button a{
    color:#fff;
}

.button:not(.button--icon) i{
    max-height: 1rem;
    max-width: 1rem;
}

.swal2-modal .swal2-cancel,
.swal2-modal .swal2-cancel:active,
.swal2-modal .swal2-cancel:focus,
.swal2-modal .swal2-cancel:hover{
    background: $button--secondary-backgroundColor;
    color: $button--secondary-text;
}

.about-section{
    margin:60px 0;
}
.inner-banner{
    background-color: #383944;
    background-image: url(../img/about-banner.jpg);
    position: relative;
    background-repeat: no-repeat;
    background-position: top right;
    background-size: 50% 100%;
    padding: 30px 0 6%;
    margin-bottom: 70px;
}
.inner-banner .heroCarousel-content::before{
